import React, { useState } from "react";

function Encode() {
  const [inputType, setInputType] = useState("text"); // "text" or "image"
  const [text, setText] = useState("");
  const [imageFile, setImageFile] = useState(null);
  const [response, setResponse] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResponse("");

    let formData = new FormData();

    if (inputType === "text") {
      formData.append("text", text);
    } else if (inputType === "image" && imageFile) {
      formData.append("image", imageFile);
    }

    // Add other form fields like medium, AI permission here later

    try {
      const res = await fetch("http://localhost:5000/encode", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      setResponse(data.message);
    } catch (err) {
      setResponse("Error connecting to server.");
    }

    setLoading(false);
  };

  return (
    <div style={{ maxWidth: "500px", margin: "auto", padding: "1rem" }}>
      <h2>Encode Your Message</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Choose Input Type:
          <select
            value={inputType}
            onChange={(e) => setInputType(e.target.value)}
          >
            <option value="text">Text</option>
            <option value="image">Image</option>
          </select>
        </label>

        {inputType === "text" && (
          <textarea
            value={text}
            onChange={(e) => setText(e.target.value)}
            placeholder="Enter your secret message"
            rows={4}
            style={{ width: "100%" }}
          />
        )}

        {inputType === "image" && (
          <input
            type="file"
            accept="image/*"
            onChange={(e) => setImageFile(e.target.files[0])}
          />
        )}

        <button type="submit" style={{ marginTop: "1rem" }} disabled={loading}>
          {loading ? "Encoding..." : "Encode"}
        </button>
      </form>

      {response && (
        <p style={{ marginTop: "1rem", whiteSpace: "pre-wrap" }}>{response}</p>
      )}
    </div>
  );
}

export default Encode;
