import React, { useState } from "react";

function Encode() {
  const [inputType, setInputType] = useState("text"); // "text" or "image"
  const [text, setText] = useState("");
  const [imageFile, setImageFile] = useState(null);
  const [medium, setMedium] = useState("WhatsApp");
  const [confidentiality, setConfidentiality] = useState("casual");
  const [allowAI, setAllowAI] = useState(true);
  const [aiDecision, setAIDecision] = useState(null);
  const [confirmed, setConfirmed] = useState(false);
  const [manualEncoding, setManualEncoding] = useState("pdf");
  const [response, setResponse] = useState("");
  const [loading, setLoading] = useState(false);
  const [encodedImage, setEncodedImage] = useState(null);

  // Step 1: Ask AI decision if allowed
  const getAIDecision = async () => {
    setLoading(true);
    setResponse("");
    let formData = new FormData();

    if (inputType === "text") formData.append("text", text);
    else if (inputType === "image" && imageFile) formData.append("image", imageFile);

    formData.append("medium", medium);
    formData.append("confidentiality", confidentiality);
    formData.append("allowAI", allowAI);

    try {
      const res = await fetch("http://localhost:5000/decide_encoding", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      setAIDecision(data.aiDecision); // corrected key here
    } catch (err) {
      setResponse("Error connecting to server.");
    }
    setLoading(false);
  };

  // Step 2: User confirms AI decision or chooses own encoding
  const handleEncode = async (e) => {
    e.preventDefault();

    if (allowAI && !confirmed) {
      // If AI allowed but no confirmation yet, get AI decision first
      await getAIDecision();
      return;
    }

    setLoading(true);
    setResponse("");
    let formData = new FormData();

    if (inputType === "text") formData.append("text", text);
    else if (inputType === "image" && imageFile) formData.append("image", imageFile);

    formData.append("medium", medium);
    formData.append("confidentiality", confidentiality);
    formData.append("allowAI", allowAI);

    // Use AI decision if allowed and confirmed, else manual encoding
    formData.append("encoding", allowAI && confirmed ? aiDecision : manualEncoding);

    try {
      const res = await fetch("http://localhost:5000/encode", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      setResponse(data.message);
      if (data.encoded_image_url) {
        setEncodedImage(data.encoded_image_url);
      } else {
        setEncodedImage(null);
      }
    } catch (err) {
      setResponse("Error connecting to server.");
      setEncodedImage(null);
    }
  };

  // Reset AI decision and confirmation when AI permission toggled
  const toggleAllowAI = () => {
    setAllowAI(!allowAI);
    setAIDecision(null);
    setConfirmed(false);
  };

  return (
    <div style={{ maxWidth: "500px", margin: "auto", padding: "1rem" }}>
      <h2>Encode Your Message</h2>
      <form onSubmit={handleEncode}>
        <label>
          Choose Input Type:
          <select
            value={inputType}
            onChange={(e) => setInputType(e.target.value)}
          >
            <option value="text">Text</option>
            <option value="image">Image</option>
          </select>
        </label>

        {inputType === "text" && (
          <textarea
            value={text}
            onChange={(e) => setText(e.target.value)}
            placeholder="Enter your secret message"
            rows={4}
            style={{ width: "100%" }}
          />
        )}

        {inputType === "image" && (
          <input
            type="file"
            accept="image/*"
            onChange={(e) => setImageFile(e.target.files[0])}
          />
        )}

        <label>
          Medium:
          <select value={medium} onChange={(e) => setMedium(e.target.value)}>
            <option value="WhatsApp">WhatsApp</option>
            <option value="Email">Email</option>
            <option value="Telegram">Telegram</option>
            <option value="Other">Other</option>
          </select>
        </label>

        <label>
          Confidentiality:
          <select
            value={confidentiality}
            onChange={(e) => setConfidentiality(e.target.value)}
          >
            <option value="casual">Casual</option>
            <option value="professional">Professional</option>
            <option value="high">High Security</option>
          </select>
        </label>

        <label>
          <input
            type="checkbox"
            checked={allowAI}
            onChange={toggleAllowAI}
          />
          Allow AI to decide encoding method
        </label>

        {!allowAI && (
          <label>
            Choose Encoding Manually:
            <select
              value={manualEncoding}
              onChange={(e) => setManualEncoding(e.target.value)}
            >
              <option value="pdf">PDF</option>
              <option value="image">Image</option>
              <option value="qr">QR Code</option>
            </select>
          </label>
        )}

        {/* Show AI decision and ask for confirmation */}
        {allowAI && aiDecision && !confirmed && (
          <div
            style={{
              marginTop: "1rem",
              backgroundColor: "#eee",
              padding: "1rem",
            }}
          >
            <p>
              AI suggests encoding using: <b>{aiDecision}</b>
            </p>
            <button
              type="button"
              onClick={() => setConfirmed(true)}
              style={{ marginRight: "1rem" }}
            >
              Confirm
            </button>
            <button
              type="button"
              onClick={() => {
                setAIDecision(null);
                setConfirmed(false);
              }}
            >
              Reject / Choose manually later
            </button>
          </div>
        )}

        <button type="submit" style={{ marginTop: "1rem" }} disabled={loading}>
          {loading ? "Processing..." : "Encode"}
        </button>
      </form>

      {response && (
        <p style={{ marginTop: "1rem", whiteSpace: "pre-wrap" }}>{response}</p>
      )}

      {/* Encoded Image Preview */}
      {encodedImage && (
        <div style={{ marginTop: "1rem" }}>
          <h3>Encoded Image Preview:</h3>
          <img
            src={encodedImage}
            alt="Encoded"
            style={{ maxWidth: "100%", border: "1px solid #ccc" }}
          />
          <br />
          <a href={encodedImage} download="encoded.png">
            Download Encoded Image
          </a>
        </div>
      )}
    </div>
  );
}

export default Encode;
