import React, { useState } from "react";

function Encode() {
  // Encoding states
  const [inputType, setInputType] = useState("text"); // "text" or "image" for encoding
  const [text, setText] = useState("");
  const [imageFile, setImageFile] = useState(null); // For secret image input type (for encoding)
  const [coverImageFile, setCoverImageFile] = useState(null); // NEW: State for uploaded cover image
  const [outputMedia, setOutputMedia] = useState("image"); // State for desired output media (image, pdf, qr)
  const [medium, setMedium] = useState("WhatsApp");
  const [confidentiality, setConfidentiality] = useState("casual");
  const [allowAI, setAllowAI] = useState(true);
  const [aiDecision, setAIDecision] = useState(null);
  const [confirmed, setConfirmed] = useState(false);
  const [manualEncoding, setManualEncoding] = useState("lsb_image"); // Default to LSB image for manual
  const [response, setResponse] = useState(""); // General response message for encoding
  const [loading, setLoading] = useState(false); // Loading state for encoding
  const [encodedImage, setEncodedImage] = useState(null); // Stores the URL of the encoded image

  // Decoding states (already implemented)
  const [decodeImageFile, setDecodeImageFile] = useState(null);
  const [decodedMessage, setDecodedMessage] = useState("");
  const [decodingLoading, setDecodingLoading] = useState(false);
  const [decodeResponse, setDecodeResponse] = useState("");


  // --- Encoding Functions ---

  const getAIDecision = async () => {
    setLoading(true);
    setResponse("");
    setEncodedImage(null); // Clear previous image on new attempt

    let formData = new FormData();
    formData.append("inputType", inputType);
    formData.append("medium", medium);
    formData.append("confidentiality", confidentiality);
    formData.append("allowAI", allowAI);
    formData.append("outputMedia", outputMedia);

    // If an image file is selected (for image-in-image) or cover image is provided, send it
    if (inputType === "image" && imageFile) formData.append("image", imageFile);
    if (coverImageFile) formData.append("coverImage", coverImageFile); // NEW: Send cover image for AI decision

    try {
      const res = await fetch("http://localhost:5000/decide_encoding", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      if (res.ok) {
        setAIDecision(data.aiDecision);
        setResponse(data.message);
      } else {
        setResponse(`Error: ${data.message || 'Failed to get AI decision.'}`);
      }
    } catch (err) {
      console.error("Error fetching AI decision:", err);
      setResponse("Error connecting to server for AI decision.");
    } finally {
      setLoading(false);
    }
  };

  const handleEncode = async (e) => {
    e.preventDefault();

    if (allowAI && aiDecision === null) {
      await getAIDecision();
      return;
    }

    if (allowAI && aiDecision !== null && !confirmed) {
        setConfirmed(true);
    }

    setLoading(true);
    setResponse("");
    setEncodedImage(null);

    let formData = new FormData();
    formData.append("inputType", inputType);

    if (inputType === "text") {
      if (!text) {
          setResponse("Please enter text to encode.");
          setLoading(false);
          return;
      }
      formData.append("text", text);
    } else if (inputType === "image") {
      if (!imageFile) {
          setResponse("Please select a secret image file to encode.");
          setLoading(false);
          return;
      }
      formData.append("image", imageFile); // This is the SECRET image
    }

    if (!coverImageFile) { // NEW: Check for cover image file
        setResponse("Please select a cover image file.");
        setLoading(false);
        return;
    }
    formData.append("coverImage", coverImageFile); // NEW: Send the COVER image

    formData.append("medium", medium);
    formData.append("confidentiality", confidentiality);
    formData.append("allowAI", allowAI);
    formData.append("outputMedia", outputMedia);
    formData.append("encoding", allowAI && confirmed ? aiDecision : manualEncoding);

    try {
      const res = await fetch("http://localhost:5000/encode", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      if (res.ok) {
        setResponse(data.message);
        if (data.encoded_image_url) {
          setEncodedImage(data.encoded_image_url);
        } else {
          setEncodedImage(null);
        }
      } else {
        setResponse(`Error: ${data.message || 'Encoding failed.'}`);
        setEncodedImage(null);
      }
    } catch (err) {
      console.error("Error during encoding:", err);
      setResponse("Error connecting to server for encoding.");
      setEncodedImage(null);
    } finally {
      setLoading(false);
      setConfirmed(false);
      setAIDecision(null);
    }
  };

  const toggleAllowAI = () => {
    setAllowAI(!allowAI);
    setAIDecision(null);
    setConfirmed(false);
  };

  // --- Decoding Functions ---
  const handleDecode = async (e) => {
    e.preventDefault();
    setDecodingLoading(true);
    setDecodedMessage("");
    setDecodeResponse("");

    if (!decodeImageFile) {
      setDecodeResponse("Please select an image to decode.");
      setDecodingLoading(false);
      return;
    }

    const formData = new FormData();
    formData.append("image", decodeImageFile);

    try {
      const res = await fetch("http://localhost:5000/decode", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      if (res.ok) {
        if (data.decoded_type === 'text') {
            setDecodedMessage(data.decoded_content);
            setDecodeResponse(data.message);
        } else if (data.decoded_type === 'image') {
            setDecodedMessage(null); // Clear text message
            // Display the decoded image directly from its URL
            setDecodeResponse(data.message);
            // You might need a new state for decoded image URL to display it separately
            setEncodedImage(data.decoded_content_url); // Reusing encodedImage for now, but better to have separate state
        }
      } else {
        setDecodedMessage("");
        setDecodeResponse(`Error: ${data.message || 'Decoding failed.'}`);
      }
    } catch (err) {
      console.error("Error during decoding:", err);
      setDecodedMessage("");
      setDecodeResponse("Error connecting to server for decoding.");
    } finally {
      setDecodingLoading(false);
    }
  };


  return (
    <div style={{ maxWidth: "800px", margin: "auto", padding: "1rem", display: "grid", gridTemplateColumns: "1fr", gap: "2rem" }}>
      {/* Encoding Section */}
      <div style={{ padding: "1.5rem", border: "1px solid #ddd", borderRadius: "8px", boxShadow: "0 2px 5px rgba(0,0,0,0.05)", backgroundColor: "#fff" }}>
        <h2 style={{ marginBottom: "1rem", textAlign: "center", color: "#333" }}>Encode Your Message</h2>
        <form onSubmit={handleEncode} style={{ display: "flex", flexDirection: "column", gap: "1rem" }}>
          <label>
            Choose Input Type:
            <select
              value={inputType}
              onChange={(e) => {
                setInputType(e.target.value);
                setText("");
                setImageFile(null); // Clear secret image when switching type
              }}
              style={{ width: "100%", padding: "0.5rem", borderRadius: "4px", border: "1px solid #ccc" }}
            >
              <option value="text">Text</option>
              <option value="image">Image</option>
            </select>
          </label>

          {inputType === "text" && (
            <textarea
              value={text}
              onChange={(e) => setText(e.target.value)}
              placeholder="Enter your secret message"
              rows={4}
              style={{ width: "100%", padding: "0.5rem", borderRadius: "4px", border: "1px solid #ccc" }}
            />
          )}

          {inputType === "image" && (
            <label>
                Upload Secret Image: {/* Renamed for clarity */}
                <input
                  type="file"
                  accept="image/*"
                  onChange={(e) => setImageFile(e.target.files[0])}
                  style={{ width: "100%", padding: "0.5rem", borderRadius: "4px", border: "1px solid #ccc" }}
                />
            </label>
          )}

          {/* NEW: Cover Image Upload */}
          <label>
            Upload Cover Image:
            <input
              type="file"
              accept="image/png, image/jpeg, image/jpg"
              onChange={(e) => setCoverImageFile(e.target.files[0])}
              required // Make cover image upload mandatory for now
              style={{ width: "100%", padding: "0.5rem", borderRadius: "4px", border: "1px solid #ccc" }}
            />
          </label>

          <label>
            Choose Output Media:
            <select
              value={outputMedia}
              onChange={(e) => setOutputMedia(e.target.value)}
              style={{ width: "100%", padding: "0.5rem", borderRadius: "4px", border: "1px solid #ccc" }}
            >
              <option value="image">Image</option>
              <option value="pdf">PDF (Coming Soon)</option>
              <option value="qr">QR Code (Coming Soon)</option>
            </select>
          </label>

          <label>
            Medium:
            <select value={medium} onChange={(e) => setMedium(e.target.value)} style={{ width: "100%", padding: "0.5rem", borderRadius: "4px", border: "1px solid #ccc" }}>
              <option value="WhatsApp">WhatsApp</option>
              <option value="Email">Email</option>
              <option value="Telegram">Telegram</option>
              <option value="Other">Other</option>
            </select>
          </label>

          <label>
            Confidentiality:
            <select
              value={confidentiality}
              onChange={(e) => setConfidentiality(e.target.value)}
              style={{ width: "100%", padding: "0.5rem", borderRadius: "4px", border: "1px solid #ccc" }}
            >
              <option value="casual">Casual</option>
              <option value="professional">Professional</option>
              <option value="high">High Security</option>
            </select>
          </label>

          <label style={{ display: "flex", alignItems: "center", gap: "0.5rem" }}>
            <input
              type="checkbox"
              checked={allowAI}
              onChange={toggleAllowAI}
            />
            Allow AI to decide encoding method
          </label>

          {!allowAI && (
            <label>
              Choose Encoding Manually:
              <select
                value={manualEncoding}
                onChange={(e) => setManualEncoding(e.target.value)}
                style={{ width: "100%", padding: "0.5rem", borderRadius: "4px", border: "1px solid #ccc" }}
              >
                <option value="lsb_image">LSB Image</option>
                <option value="pdf_meta">PDF Metadata (Coming Soon)</option>
                <option value="qr_stego">QR Code Stego (Coming Soon)</option>
              </select>
            </label>
          )}

          {allowAI && aiDecision && !confirmed && (
            <div
              style={{
                marginTop: "1rem",
                backgroundColor: "#f0f0f0",
                padding: "1rem",
                borderRadius: "8px",
                border: "1px solid #ddd",
                textAlign: "center"
              }}
            >
              <p style={{ margin: "0 0 1rem 0" }}>
                AI suggests encoding using: <b style={{ color: "#007bff" }}>{aiDecision}</b>
              </p>
              <button
                type="button"
                onClick={() => setConfirmed(true)}
                style={{
                  marginRight: "1rem",
                  padding: "0.5rem 1rem",
                  borderRadius: "5px",
                  border: "none",
                  backgroundColor: "#28a745",
                  color: "white",
                  cursor: "pointer",
                }}
              >
                Confirm
              </button>
              <button
                type="button"
                onClick={() => {
                  setAIDecision(null);
                  setConfirmed(false);
                }}
                style={{
                  padding: "0.5rem 1rem",
                  borderRadius: "5px",
                  border: "1px solid #dc3545",
                  backgroundColor: "transparent",
                  color: "#dc3545",
                  cursor: "pointer",
                }}
              >
                Reject / Choose manually
              </button>
            </div>
          )}

          <button
            type="submit"
            style={{
              marginTop: "1rem",
              padding: "0.8rem 1.5rem",
              borderRadius: "5px",
              border: "none",
              backgroundColor: "#007bff",
              color: "white",
              fontSize: "1rem",
              cursor: "pointer",
              transition: "background-color 0.3s ease",
            }}
            disabled={loading}
          >
            {loading ? "Processing..." : "Encode"}
          </button>
        </form>

        {response && (
          <p style={{ marginTop: "1rem", padding: "0.5rem", borderRadius: "4px", backgroundColor: "#e9ecef", whiteSpace: "pre-wrap" }}>
            {response}
          </p>
        )}

        {encodedImage && (
          <div style={{ marginTop: "1rem", textAlign: "center" }}>
            <h3>Encoded Image Preview:</h3>
            <img
              src={encodedImage}
              alt="Encoded"
              style={{ maxWidth: "100%", height: "auto", border: "1px solid #ccc", borderRadius: "8px", marginTop: "0.5rem" }}
            />
            <br />
            <a
              href={encodedImage}
              download="encoded.png"
              style={{
                display: "inline-block",
                marginTop: "1rem",
                padding: "0.5rem 1rem",
                borderRadius: "5px",
                border: "1px solid #007bff",
                backgroundColor: "transparent",
                color: "#007bff",
                textDecoration: "none",
                cursor: "pointer",
              }}
            >
              Download Encoded Image
            </a>
          </div>
        )}
      </div>

      {/* Decoding Section */}
      <div style={{ padding: "1.5rem", border: "1px solid #ddd", borderRadius: "8px", boxShadow: "0 2px 5px rgba(0,0,0,0.05)", backgroundColor: "#fff" }}>
        <h2 style={{ marginBottom: "1rem", textAlign: "center", color: "#333" }}>Decode Your Message</h2>
        <form onSubmit={handleDecode} style={{ display: "flex", flexDirection: "column", gap: "1rem" }}>
          <label>
            Upload Encoded Image:
            <input
              type="file"
              accept="image/png, image/jpeg, image/jpg"
              onChange={(e) => setDecodeImageFile(e.target.files[0])}
              style={{ width: "100%", padding: "0.5rem", borderRadius: "4px", border: "1px solid #ccc" }}
            />
          </label>

          <button
            type="submit"
            style={{
              marginTop: "1rem",
              padding: "0.8rem 1.5rem",
              borderRadius: "5px",
              border: "none",
              backgroundColor: "#28a745",
              color: "white",
              fontSize: "1rem",
              cursor: "pointer",
              transition: "background-color 0.3s ease",
            }}
            disabled={decodingLoading}
          >
            {decodingLoading ? "Decoding..." : "Decode Image"}
          </button>
        </form>

        {decodeResponse && (
          <p style={{ marginTop: "1rem", padding: "0.5rem", borderRadius: "4px", backgroundColor: "#e9ecef", whiteSpace: "pre-wrap" }}>
            {decodeResponse}
          </p>
        )}

        {decodedMessage && (
          <div style={{ marginTop: "1rem", padding: "1rem", border: "1px solid #007bff", borderRadius: "8px", backgroundColor: "#e6f7ff" }}>
            <h3>Decoded Message:</h3>
            <p style={{ fontWeight: "bold", wordBreak: "break-all" }}>{decodedMessage}</p>
          </div>
        )}
         {/* Display decoded image if type is image */}
         {decodedMessage === null && encodedImage && ( // encodedImage state is being reused for decoded image URL
            <div style={{ marginTop: "1rem", textAlign: "center" }}>
                <h3>Decoded Secret Image:</h3>
                <img
                src={encodedImage} // This now points to the decoded secret image URL
                alt="Decoded Secret"
                style={{ maxWidth: "100%", height: "auto", border: "1px solid #ccc", borderRadius: "8px", marginTop: "0.5rem" }}
                />
                <br />
                <a
                href={encodedImage}
                download="decoded_secret_image.png"
                style={{
                    display: "inline-block",
                    marginTop: "1rem",
                    padding: "0.5rem 1rem",
                    borderRadius: "5px",
                    border: "1px solid #28a745",
                    backgroundColor: "transparent",
                    color: "#28a745",
                    textDecoration: "none",
                    cursor: "pointer",
                }}
                >
                Download Decoded Secret Image
                </a>
            </div>
         )}
      </div>
    </div>
  );
}

export default Encode;
